import React, { useState } from 'react';
import axios from 'axios';
import {signUp} from '../../utils/API';
import {signParams} from "../../utils/configs";

const DriverSignup = () => {
    const [fields, setFields] = useState({
        ...signParams
    })

    const onClickBtn = (event) => {
        event.preventDefault()
        SignUp()
          .then()
    }
    
    const onClick = (event) => {
    }
    
    const onChange = (event) => {
        console.log(event.target.id)
        const params = {
            ...fields,
            alert: ''
        }
        params[event.target.id] = event.target.value
        setFields(params)
        console.log(fields)
    }
    
    const SignUp = async () => {
        const params = {
            driver: {
                ...fields
            }
        }
        await signUp(params)
          .then(response => console.log(response))
          .catch(response => {
              setFields({ ...fields, alert: response.message })
          })
    }

    return (
        <>
            <form className="w-25 border position-absolute top-50 start-50 translate-middle">
                <div className="me-3 mx-3">
                    <br />
                    <h5>Driver registration</h5>
                    <label htmlFor="phone" className="form-label">Phone</label>
                    <input onClick={
                        (e) => onClick(e)
                    }
                           onChange={
                               (e) => onChange(e, 'phone_number')
                           }
                           id="phone_number"
                           type="number"
                           className="form-control"/>
                    <label htmlFor="name" className="form-label">Username</label>
                    <input onClick={
                        () => { setFields({ ...fields, alert: '' })
                        }}
                           onChange={
                               (e) => onChange(e)
                           }
                           id="first_name"
                           type="text"
                           className="form-control"/>
                    {/*<label htmlFor="password" className="form-label">Password</label>*/}
                    {/*<input onClick={() => { setFields({ ...fields, alert: '' }) }} onChange={*/}
                    {/*    (e) => onChange(e)*/}
                    {/*} id="password" type="password" className="form-control" value={fields.password} />*/}
                    {/*<label htmlFor="cPassword" className="form-label">Confirm Password</label>*/}
                    {/*<input onClick={() => { setFields({ ...fields, alert: '' }) }} onChange={(e) => { setFields({ ...fields, cPassword: e.target.value, alert: '' }) }} htmlFor="cPassword" type="password" className="form-control" value={fields.cPassword} />*/}
                    {/*<p>{fields.alert}</p>*/}
                </div>
                <button onClick={(e) => { onClickBtn(e) }} type="submit" className="btn btn-outline-success mx-3 mb-3">Submit</button>
            </form>
        </>
    )
}

export default DriverSignup;
