import React from 'react'
import {login} from "./API";
import {ChangeActionLoading, ChangeActionLoggedIn} from "../redux/actions";

const onClick = (event, fields, setFields, dispatch) => {
  event.preventDefault();
  Login(fields, setFields, dispatch)
    .then()
}

const Login = async (props) => {
  const params = {
    session: {
      ...props.fields,
      remember_me: props.fields.remember_me === true ? '1': '0'
    }
  }
  await login(params)
    .then(response => {
      props.dispatch(ChangeActionLoggedIn({
        ...props.state,
        'isLoading': false,
        'loggedIn': true,
        'userType': response.data.model_name,
        'userId': response.data.user.id,
        'alert': ''
      }))
      history.push(`/${response.data.model_name}/${response.data.user.id}`)
    })
    .catch(response => {
      props.dispatch(ChangeActionLoading(false))
      props.setFields({...props.fields, alert: response.message})
    })
}

export default onClick
