import React from 'react'
import { BrowserRouter } from 'react-router-dom';
import AppRoutes from './AppRoutes'
import { createStore } from 'redux'
import { connect, Provider } from 'react-redux'

const ACTION_CHANGE_LOGGED_IN = 'ACTION_CHANGE_LOGGED_IN'
const ACTION_CHANGE_LOADING = 'ACTION_CHANGE_LOADING'

const initState = {
  isLoading: true,
  'loggedIn': false,
  'userType': '',
  'userId': ''
}
const rootReducer = (state = initState, action) => {
  switch (action.type) {
    case ACTION_CHANGE_LOGGED_IN:
      return {...state, loggedIn: action.payload}
    case ACTION_CHANGE_LOADING:
      return {...state, isLoading: action.payload}
  }
  return state
}

const store = createStore(rootReducer)

const mapStateToProps = (state) => {
  const appState = state
  console.log(appState, ' redux mapState')
  return {
    appState
  }
}
const WrappedApp = connect(mapStateToProps)(AppRoutes)

const App = () => {
    return (
      <Provider store={store}>
        <BrowserRouter>
            <WrappedApp />
        </BrowserRouter>
      </Provider>
    )
}

export default App
