import React, { useState } from 'react';
import axios from 'axios';
import {signUp} from '../../utils/API';
import {signParams} from "../../utils/configs";
import RegistrationForm from "../RegistrationForm";

const DriverSignup = () => {
    const [fields, setFields] = useState({
        ...signParams
    })

    const onClickBtn = (event) => {
        event.preventDefault()
        SignUp()
          .then()
    }
    
    const onClick = (event) => {
    }
    
    const onChange = (event) => {
        console.log(event.target.id)
        const params = {
            ...fields,
            alert: ''
        }
        params[event.target.id] = event.target.value
        setFields(params)
        console.log(fields)
    }
    
    const SignUp = async () => {
        const params = {
            driver: {
                ...fields
            }
        }
        await signUp(params)
          .then(response => console.log(response))
          .catch(response => {
              console.log('catch')
              if (!response.created) {
                  
              }
              setFields({ ...fields, alert: '' })
          })
    }

    return (
        <>
            <form className="w-25 border position-absolute top-50 start-50 translate-middle">
                <div className="me-3 mx-3">
                    <RegistrationForm onChange={onChange}/>
                    <p>{fields.alert}</p>
                </div>
                <button onClick={(e) => { onClickBtn(e) }}
                        type="submit"
                        className="btn btn-outline-success mx-3 mb-3">Submit</button>
            </form>
        </>
    )
}

export default DriverSignup;
